// App.tsx
import React, { useState, useEffect } from 'react';
import { Toaster } from 'react-hot-toast';
import { AppLayout } from './components/layout/AppLayout';
import { KeywordInput } from './components/stages/KeywordInput';
import { ResearchDisplay } from './components/stages/ResearchDisplay';
import { BlogDisplay } from './components/stages/BlogDisplay';
import { PodcastDisplay } from './components/stages/PodcastDisplay';
import { AudioPlayer } from './components/stages/AudioPlayer';
import { ImageGallery } from './components/stages/ImageGallery';
import { SocialCaptions } from './components/stages/SocialCaptions';
import { ContentSummary } from './components/final/ContentSummary';
import { useProject } from './hooks/useProject';
import { ProjectStage } from './types/project.types';

function App() {
  const {
    project,
    currentStage,
    isLoading,
    error,
    createProject,
    updateStage,
    getStageContent,
    regenerateContent
  } = useProject();

  const [stageContent, setStageContent] = useState<Record<string, any>>({});

  useEffect(() => {
    if (project?.id) {
      loadAllStageContent();
    }
  }, [project?.id, getStageContent]);

  const loadAllStageContent = async () => {
    if (!project?.id) return;

    const stages: ProjectStage[] = [
      'research', 'blog', 'podcast_script', 
      'audio', 'images', 'social'
    ];

    const content: Record<string, any> = {};

    for (const stage of stages) {
      const data = await getStageContent(project.id, stage);
      if (data) {
        content[stage] = data;
      }
    }

    setStageContent(content);
  };

  const handleKeywordSubmit = async (keyword: string) => {
    const newProject = await createProject(keyword);
    if (newProject) {
      // Automatically trigger research
      await handleGenerateResearch(newProject.id, keyword);
    }
  };

  const handleGenerateResearch = async (projectId: string, keyword: string) => {
    const research = await regenerateContent(projectId, 'research', { keyword });
    if (research) {
      setStageContent(prev => ({ ...prev, research }));
    }
  };

  const handleGenerateBlog = async () => {
    if (!project?.id || !stageContent.research) return;

    const blog = await regenerateContent(project.id, 'blog', {
      keyword: project.keyword,
      research: stageContent.research
    });

    if (blog) {
      setStageContent(prev => ({ ...prev, blog }));
      await updateStage(project.id, 'blog');
    }
  };

  const handleGeneratePodcast = async () => {
    if (!project?.id || !stageContent.blog) return;

    const podcast = await regenerateContent(project.id, 'podcast_script', {
      keyword: project.keyword,
      blog: stageContent.blog
    });

    if (podcast) {
      setStageContent(prev => ({ ...prev, podcast_script: podcast }));
      await updateStage(project.id, 'podcast_script');
    }
  };

  const handleGenerateAudio = async () => {
    if (!project?.id || !stageContent.podcast_script) return;

    const audio = await regenerateContent(project.id, 'audio', {
      keyword: project.keyword,
      podcastScript: stageContent.podcast_script
    });

    if (audio) {
      setStageContent(prev => ({ ...prev, audio }));
      await updateStage(project.id, 'audio');
    }
  };

  const handleGenerateImages = async () => {
    if (!project?.id || !stageContent.blog) return;

    const images = await regenerateContent(project.id, 'images', {
      keyword: project.keyword,
      content: stageContent.blog
    });

    if (images) {
      setStageContent(prev => ({ ...prev, images }));
      await updateStage(project.id, 'images');
    }
  };

  const handleGenerateSocial = async () => {
    if (!project?.id || !stageContent.blog) return;

    const social = await regenerateContent(project.id, 'social', {
      keyword: project.keyword,
      content: stageContent.blog
    });

    if (social) {
      setStageContent(prev => ({ ...prev, social }));
      await updateStage(project.id, 'social');
    }
  };

  const handleCompleteProject = async () => {
    if (!project?.id) return;
    await updateStage(project.id, 'complete');
  };

  const renderCurrentStage = () => {
    if (!project) {
      return <KeywordInput onSubmit={handleKeywordSubmit} isLoading={isLoading} />;
    }

    switch (currentStage) {
      case 'research':
        return (
          <ResearchDisplay
            research={stageContent.research}
            isLoading={isLoading}
            onRegenerate={() => handleGenerateResearch(project.id, project.keyword)}
            onContinue={handleGenerateBlog}
          />
        );

      case 'blog':
        return (
          <BlogDisplay
            blog={stageContent.blog}
            isLoading={isLoading}
            onRegenerate={handleGenerateBlog}
            onContinue={handleGeneratePodcast}
          />
        );

      case 'podcast_script':
        return (
          <PodcastDisplay
            podcast={stageContent.podcast_script}
            isLoading={isLoading}
            onRegenerate={handleGeneratePodcast}
            onContinue={handleGenerateAudio}
          />
        );

      case 'audio':
        return (
          <AudioPlayer
            audio={stageContent.audio}
            isLoading={isLoading}
            onRegenerate={handleGenerateAudio}
            onContinue={handleGenerateImages}
          />
        );

      case 'images':
        return (
          <ImageGallery
            images={stageContent.images}
            isLoading={isLoading}
            onRegenerate={handleGenerateImages}
            onContinue={handleGenerateSocial}
          />
        );

      case 'social':
        return (
          <SocialCaptions
            social={stageContent.social}
            isLoading={isLoading}
            onRegenerate={handleGenerateSocial}
            onContinue={handleCompleteProject}
          />
        );

      case 'complete':
        return (
          <ContentSummary
            project={project}
            onNewProject={() => window.location.reload()}
          />
        );

      default:
        return <div>Unknown stage</div>;
    }
  };

  return (
    <div className="min-h-screen bg-gray-950 bg-gradient-to-br from-gray-900 via-gray-950 to-[#1a0d3d]">
      <AppLayout 
        project={project}
      >
        <div className="max-w-4xl mx-auto">
          {error && (
            <div className="mb-6 p-4 bg-red-500/10 backdrop-blur-xl border border-red-500/20 rounded-2xl text-red-400">
              {error}
            </div>
          )}

          {renderCurrentStage()}
        </div>
      </AppLayout>

      <Toaster 
        position="bottom-right"
        toastOptions={{
          style: {
            background: 'rgba(20, 20, 20, 0.9)',
            color: '#fff',
            backdropFilter: 'blur(10px)',
            border: '1px solid rgba(255, 255, 255, 0.1)',
          },
        }}
      />
    </div>
  );
}

export default App;
