import React, { useState } from 'react';
import { Copy, Download, RefreshCw, ArrowRight, Loader2, FileText, Clock } from 'lucide-react';
import { BlogContent } from '../../types/project.types';
import { toast } from 'react-toastify';

interface BlogDisplayProps {
  blog: BlogContent | null;
  isLoading: boolean;
  onRegenerate: () => Promise<void>;
  onContinue: () => void;
}

export const BlogDisplay: React.FC<BlogDisplayProps> = ({
  blog,
  isLoading,
  onRegenerate,
  onContinue,
}) => {
  const [isCopying, setIsCopying] = useState(false);

  const handleCopy = async () => {
    if (!blog) return;

    setIsCopying(true);
    try {
      await navigator.clipboard.writeText(blog.content);
      toast.success('Blog content copied to clipboard!');
    } catch (error) {
      toast.error('Failed to copy content');
    } finally {
      setIsCopying(false);
    }
  };

  const handleDownload = () => {
    if (!blog) return;

    const blob = new Blob([blog.content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${blog.title.replace(/\s+/g, '-').toLowerCase()}.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
    toast.success('Blog downloaded successfully!');
  };

  if (isLoading && !blog) {
    return (
      <div className="flex items-center justify-center min-h-[400px]">
        <div className="text-center">
          <Loader2 className="w-12 h-12 animate-spin text-blue-600 mx-auto mb-4" />
          <p className="text-gray-600">Generating blog content...</p>
        </div>
      </div>
    );
  }

  if (!blog) {
    return (
      <div className="text-center py-12">
        <p className="text-gray-600 mb-4">No blog content available</p>
        <button
          onClick={onRegenerate}
          disabled={isLoading}
          className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
        >
          {isLoading ? (
            <Loader2 className="w-4 h-4 mr-2 animate-spin" />
          ) : (
            <RefreshCw className="w-4 h-4 mr-2" />
          )}
          Generate Blog
        </button>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-6 flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Blog Post</h2>
          <div className="flex items-center gap-4 mt-2 text-sm text-gray-600">
            <span className="flex items-center gap-1">
              <FileText className="w-4 h-4" />
              {blog.wordCount.toLocaleString()} words
            </span>
            <span className="flex items-center gap-1">
              <Clock className="w-4 h-4" />
              {blog.readingTime} min read
            </span>
          </div>
          {blog.targetKeywords && blog.targetKeywords.length > 0 && (
            <div className="flex items-center gap-2 mt-2">
              <span className="text-sm text-gray-500">Keywords:</span>
              {blog.targetKeywords.map((kw, i) => (
                <span key={i} className="text-xs px-2 py-1 bg-blue-100 text-blue-800 rounded">
                  {kw}
                </span>
              ))}
            </div>
          )}
        </div>
        <div className="flex items-center gap-3">
          <button
            onClick={handleCopy}
            disabled={isCopying}
            className="inline-flex items-center px-4 py-2 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50"
          >
            <Copy className="w-4 h-4 mr-2" />
            {isCopying ? 'Copying...' : 'Copy'}
          </button>
          <button
            onClick={handleDownload}
            className="inline-flex items-center px-4 py-2 bg-white border border-gray-300 rounded-lg hover:bg-gray-50"
          >
            <Download className="w-4 h-4 mr-2" />
            Download
          </button>
          <button
            onClick={onRegenerate}
            disabled={isLoading}
            className="inline-flex items-center px-4 py-2 bg-white border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50"
          >
            {isLoading ? (
              <Loader2 className="w-4 h-4 mr-2 animate-spin" />
            ) : (
              <RefreshCw className="w-4 h-4 mr-2" />
            )}
            Regenerate
          </button>
          <button
            onClick={onContinue}
            className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Continue to Podcast
            <ArrowRight className="w-4 h-4 ml-2" />
          </button>
        </div>
      </div>

      <div className="bg-white border border-gray-200 rounded-lg shadow-sm p-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-6">{blog.title}</h1>
        <div className="prose prose-lg max-w-none">
          {blog.content.split('\n\n').map((paragraph, index) => (
            <p key={index} className="mb-4 text-gray-700 leading-relaxed">
              {paragraph}
            </p>
          ))}
        </div>
      </div>
    </div>
  );
};
